create table admin_action_logs
(
    id          bigint unsigned auto_increment comment '操作日志ID'
        primary key,
    type        varchar(5)      default ''                not null comment '操作类型',
    user_id     bigint unsigned default 0                 not null comment '用户ID',
    action_desc text                                      not null comment '操作描述',
    ip          varchar(45)     default ''                not null comment 'ip地址',
    created_at  timestamp       default CURRENT_TIMESTAMP not null comment '创建时间'
)
    collate = utf8mb4_unicode_ci
    auto_increment = 161;

create index admin_action_logs_user_id_index
    on admin_action_logs (user_id);

