create table attachments
(
    id          bigint unsigned auto_increment comment '附件 id'
        primary key,
    uuid        char(36)                     not null comment 'uuid',
    user_id     bigint unsigned              not null comment '用户 id',
    type_id     bigint unsigned   default 0  not null comment '类型数据ID(post_id,dialog_message_id…)',
    `order`     tinyint unsigned  default 0  not null comment '附件排序',
    type        smallint unsigned default 0  not null comment '附件类型(0帖子附件，1帖子图片，2帖子视频，3帖子音频，4消息图片)',
    is_remote   tinyint unsigned  default 0  not null comment '是否远程附件',
    is_approved tinyint unsigned  default 1  not null comment '是否合法',
    attachment  varchar(255)      default '' not null comment '文件系统生成的名称',
    file_path   varchar(255)      default '' not null comment '文件路径',
    file_name   varchar(255)      default '' not null comment '文件原名称',
    file_size   int unsigned      default 0  not null comment '文件大小',
    file_width  bigint unsigned   default 0  not null comment '宽度',
    file_height bigint unsigned   default 0  not null comment '高度',
    file_type   varchar(255)      default '' not null comment '文件类型',
    ip          varchar(45)       default '' not null comment 'ip 地址',
    created_at  datetime                     not null comment '创建时间',
    updated_at  datetime                     not null comment '更新时间'
)
    collate = utf8mb4_unicode_ci
    auto_increment = 95;

