create table notification_tpls
(
    id            bigint unsigned auto_increment
        primary key,
    notice_id     varchar(255)                null comment '模板唯一标识ID',
    status        tinyint          default 0  not null comment '模板状态:1开启0关闭',
    type          tinyint unsigned default 0  not null comment '通知类型:0系统1微信2短信',
    type_name     varchar(255)     default '' not null comment '类型名称',
    title         varchar(255)     default '' not null comment '标题',
    content       text                        null comment '内容',
    vars          varchar(255)     default '' not null comment '可选的变量',
    template_id   varchar(255)     default '' not null comment '模板ID',
    first_data    varchar(255)     default '' not null comment 'first.DATA',
    keywords_data text                        null comment 'keywords.DATA',
    remark_data   varchar(255)     default '' not null comment 'remark.DATA',
    color         varchar(255)     default '' not null comment 'data color',
    redirect_type tinyint          default 0  not null comment '跳转类型：0无跳转 1跳转H5 2跳转小程序',
    redirect_url  varchar(255)     default '' not null comment '跳转地址',
    page_path     varchar(255)     default '' not null comment '跳转路由',
    is_error      tinyint          default 0  not null comment '模板是否配置错误',
    error_msg     text                        null comment '错误信息',
    push_type     tinyint          default 0  not null comment '消息推送类型(0:即时推送,1:间隔推送)',
    delay_time    int unsigned     default 0  not null comment '间隔推送延迟时间(秒)',
    constraint uk_notice_id
        unique (notice_id)
)
    collate = utf8mb4_unicode_ci
    auto_increment = 103;

