create table notifications
(
    id              bigint unsigned auto_increment comment '通知 id'
        primary key,
    type            varchar(255)    not null comment '通知类型',
    notifiable_type varchar(255)    not null,
    notifiable_id   bigint unsigned not null,
    data            text            not null comment '通知内容',
    read_at         datetime        null comment '通知阅读时间',
    created_at      datetime        not null comment '创建时间',
    updated_at      datetime        not null comment '更新时间'
)
    collate = utf8mb4_unicode_ci
    auto_increment = 449;

create index notifications_notifiable_type_notifiable_id_index
    on notifications (notifiable_type, notifiable_id);

