create table threads
(
    id                  bigint unsigned auto_increment comment '主题 id'
        primary key,
    user_id             bigint unsigned                                  null comment '创建用户 id',
    last_posted_user_id bigint unsigned                                  null comment '最后回复用户 id',
    category_id         int unsigned                                     null comment '分类 id',
    type                tinyint unsigned       default 0                 not null comment '类型：0普通 1长文 2视频 3图片',
    title               varchar(255)           default ''                not null comment '标题',
    price               decimal(8, 2) unsigned default 0.00              not null comment '价格',
    attachment_price    decimal(8, 2) unsigned default 0.00              not null comment '附件价格',
    free_words          double unsigned        default '0'               not null comment '免费字数百分比',
    post_count          int unsigned           default 0                 not null comment '回复数',
    view_count          int unsigned           default 0                 not null comment '查看数',
    rewarded_count      int unsigned           default 0                 not null comment '打赏数',
    paid_count          int unsigned           default 0                 not null comment '付费数',
    share_count         int unsigned           default 0                 not null comment '分享数',
    longitude           decimal(10, 7)         default 0.0000000         not null comment '经度',
    latitude            decimal(10, 7)         default 0.0000000         not null comment '纬度',
    address             varchar(100)                                     not null comment '地址',
    location            varchar(100)                                     not null comment '位置',
    posted_at           datetime                                         null comment '最新评论时间',
    created_at          datetime                                         not null comment '创建时间',
    updated_at          datetime                                         not null comment '更新时间',
    deleted_at          datetime                                         null comment '删除时间',
    issue_at            datetime               default CURRENT_TIMESTAMP not null comment '帖子首次发布、草稿箱发布、审核通过发布，重新编辑内容发布，四种变更的时间记录',
    deleted_user_id     bigint unsigned                                  null comment '删除用户 id',
    is_approved         tinyint unsigned       default 1                 not null comment '是否合法',
    is_sticky           tinyint unsigned       default 0                 not null comment '是否置顶',
    is_essence          tinyint unsigned       default 0                 not null comment '是否加精',
    is_site             tinyint unsigned       default 0                 not null comment '是否推荐到首页（0否 1是）',
    is_anonymous        tinyint                default 0                 not null comment '是否匿名 0否 1是',
    is_display          tinyint                default 1                 not null comment '是否显示 0否 1是',
    is_red_packet       tinyint unsigned       default 0                 not null comment '是否添加红包，0未添加，1添加',
    is_draft            tinyint unsigned       default 0                 not null comment '是否为草稿，0不是，1是',
    source              tinyint unsigned       default 0                 not null comment '0 站内;1 微博;2 贴吧;3 豆瓣',
    constraint threads_deleted_user_id_foreign
        foreign key (deleted_user_id) references users (id)
            on delete set null,
    constraint threads_last_posted_user_id_foreign
        foreign key (last_posted_user_id) references users (id)
            on delete set null,
    constraint threads_user_id_foreign
        foreign key (user_id) references users (id)
            on delete set null
)
    collate = utf8mb4_unicode_ci
    auto_increment = 180;

create index idx_category_id
    on threads (category_id);

create index idx_is_sticky
    on threads (is_sticky);

